<script>
	function toDouble(){ 
		if (document.getElementById("real"))
		{	
			var i = document.getElementById("real").innerHTML;
			document.getElementById("real").innerHTML = Math.round(i,1);
		}
	}

	function toDate(){ 
		if (document.getElementById("date"))
		{		
			var tarih = document.getElementById("date").innerHTML;
			var Dizi = tarih.split(" ");
			document.getElementById("date").innerHTML = Dizi[0];
		}
	}	

	function toString(){ 
		if (document.getElementById("string"))
		{
			var i = document.getElementById("string").innerHTML;
			if (i=='' || i=='0' ||  i == null) 
			{
				document.getElementById("string").innerHTML = '-';
			}
			else{
				document.getElementById("string").innerHTML = i;		
			}
		}
	}
	// Buradan tetikleme ypaılıyor
	toDouble();
	toString();
	toDate();
</script>
#SDK_END

