<script>
	function toDouble(){
		var myList = document.getElementsByTagName("span");
		for(var i=0; i<myList.length; i++){
			if (myList[i].getAttribute('id')==='real'){
				myList[i].innerHTML=parseFloat(myList[i].innerHTML).toFixed(2); //2 haneye yuvarlamak için
			}
		}
	}
	function toDate(){
		var myList = document.getElementsByTagName("span");
		for(var i=0; i<myList.length; i++){
			if (myList[i].getAttribute('id')==='date'){
				var tarih=myList[i].innerHTML;
				var Dizi=tarih.split(" ");
				myList[i].innerHTML=Dizi[0];
			}
		}
	}
	function toString(){
		var myList = document.getElementsByTagName("span");
		for(var i=0; i<myList.length; i++){
			if (myList[i].getAttribute('id')==='string'){
				var icerik=myList[i].innerHTML;
				if (icerik==' ' || icerik=='' || icerik=='0' || icerik== null){
					myList[i].innerHTML = '-';
				}
			}
		}
	}
	// Buradan tetikleme yapılıyor
	toDouble();
	toString();
	toDate();
</script>
#SDK_END

